class Monoid(n: Set[Any], f:(Any,Any) => Any, identity: Any){          
    val èmonoide = identità && chiusura && associatività               
    //verifica che l'elemento identità funzioni f(a,i) = f(i,a) = a    
    def identità : Boolean =  {                                        
        n.foreach(x => if (!(f(x,identity) == (f(identity,x)) && f(identity,x) == (x))) 
        return false)                                                  
        return true                                                    
    }                                                                  
    //per due qualsiasi numeri a e b f(a,b) appartiene al set          
    def chiusura : Boolean = {                                         
        n.foreach(x => n.foreach (y => if(!(n.contains(f(x,y)))) return false))
        return true                                                                                                        
    }                                                                                                                      
    //associatività : f(f(a,b),c) = f(a,(b,c))                                                                             
    def associatività : Boolean = {                                                                                        
        n.foreach(a => n.foreach(b => n.foreach(c => if(!(f(f(a,b),c) == f(a,f(b,c)))) return false)))                     
        return true                                                                                                        
    }                                                                                                                      
}                                                                                                                          
                                                                                                                           
class Group (set: Set[Any], f:(Any, Any) => Any, id: Any){                                                                 
    //verifica della chiusura per ogni a,b a+b da un elemento del gruppo                                                   
    def chiusura = Boolean => set.forall(a => set.forall(b => set.contains(f(a,b))))                                        
                                                                                                                           
    //verifica della associatività                                                                                         
    val associatività : Boolean = set.forall(a => set.forall(b => set.forall(c => f(f(a,b),c) == f(a,f(b,c)))))   
                                                                                                                  
    //verifica della identità                                                                                              
    val identità : Boolean = set.forall(a => f(a,id) == a && f(id,a) == a)                                  
                                                                                                       
    //verifica della invertibilità                                                                     
    val invertibilità : Boolean = set.forall(a => set.exists(e => f(a,e) == f(e,a)))                                                      
}                                                                                                                          
                                                                                                                           
